package com.sun.mystarterhomeimport android.annotation.SuppressLintimport android.os.Bundleimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.Imageimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.material.Dividerimport androidx.compose.material.FloatingActionButtonimport androidx.compose.material.Iconimport androidx.compose.material.MaterialThemeimport androidx.compose.material.Scaffoldimport androidx.compose.material.Surfaceimport androidx.compose.material.Textimport androidx.compose.material.TopAppBarimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.AccountCircleimport androidx.compose.material.icons.filled.Emailimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.compose.rememberNavControllerimport com.sun.mystarterhome.ui.theme.MyStarterHomeThemeclass GreetingActivity : ComponentActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContent {            MyStarterHomeTheme {                // A surface container using the 'background' color from the theme                Surface {                    GreetingBody()                }            }        }    }}@SuppressLint("UnusedMaterialScaffoldPaddingParameter")@Composablefun GreetingBody() {    val navController = rememberNavController()    Scaffold(        backgroundColor = Color(0xffeeeeee),        topBar = { GreetingTopBar()  },        content = {            Column(                modifier = Modifier                    .padding(top = 0.dp),            ) {                Text(                    text = "Active",                    fontSize = 20.sp,                    modifier = Modifier                        .padding(top = 10.dp, start = 15.dp, end = 15.dp)                )                Row (                    modifier = Modifier                        .fillMaxWidth()                        .padding(top = 10.dp,)                        .background(Color.White),                ) {                    Icon(imageVector = Icons.Default.Email,                        contentDescription = "account icon",                        modifier = Modifier                            .padding(top = 35.dp, start = 20.dp),)                    Column(                        modifier = Modifier                            .fillMaxWidth()                            .weight(1f)                    ) {                        Text(                            text = "Greeting",                            modifier = Modifier                                .padding(top = 20.dp, start = 20.dp, end = 40.dp, bottom = 5.dp),                            textAlign = TextAlign.Center,                            color = Color(0xff000000)                        )                        Text(                            text = "Last Run: Never",                            modifier = Modifier                                .padding(start = 20.dp, end = 40.dp, bottom = 20.dp),                            textAlign = TextAlign.Center,                            color = Color(0xffaeaeae)                        )                    }                }            }        },        bottomBar = { Bottom2Bar(navController) }    )}@Composablefun GreetingTopBar(){    TopAppBar(        title = {            Text(                text = "My Starter Home",                style = MaterialTheme.typography.h6,                color = Color.White,                modifier = Modifier.fillMaxWidth(),                textAlign = TextAlign.Center            )        },        modifier = Modifier.fillMaxWidth(),        backgroundColor = Color(0xffffe400),    )}